<script>
  import menuComp from "../elements/menu-comp";
  import IconList from "./social-icon-list";

  export default {
    name: "header-comp",
    components: {
      menuComp,
      IconList,
    },
    data: () => ({
      menuOpened: false,
      headerForm: false,
      showPhone: false,
      callbackFirst: false,
      isPageScrolled: false,
      scrollTimeoutid: null,
      lastScrolledPos: 0,
    }),
    created() {
      window.addEventListener("scroll", this.onPageScroll);
    },
    destroyed() {
      window.removeEventListener("scroll", this.onPageScroll);
    },
    computed: {
      isToggleIconVisible() {
        return this.$store.state.showMenuToggleIcon;
      },
    },
    methods: {
      onPageScroll(e) {
        if (this.scrollTimeoutid) clearTimeout(this.scrollTimeoutid);
        this.scrollTimeoutid = setTimeout(() => {
          if (window.pageYOffset > 300) {
            this.isPageScrolled = true;
          } else {
            this.isPageScrolled = false;
          }

          this.setVisibilityOfBurgerIcon();
        }, 200);
      },
      onMenuScroll() {
        ("X");
      },
      setVisibilityOfBurgerIcon() {
        // debugger;
        const isHomePage = this.$route.name === "home";
        if (!isHomePage) {
          this.$store.state.showMenuToggleIcon = true;
          return;
        }

        const icon = this.$refs.burgerIcon.$el;
        const isExcursionInfoVisible = document.querySelector(
          ".excursionInfo .close"
        );
        if (!isExcursionInfoVisible) {
          this.$store.state.showMenuToggleIcon = true;
          return;
        }
        const iconExcursionPosition = this.offset(isExcursionInfoVisible);
        const iconPosition = this.offset(icon);
        const diff = Math.abs(iconPosition - iconExcursionPosition);
        this.$store.state.showMenuToggleIcon = diff > 100;
      },

      offset(el) {
        var rect = el.getBoundingClientRect(),
          scrollLeft =
            window.pageXOffset || document.documentElement.scrollLeft,
          scrollTop = window.pageYOffset || document.documentElement.scrollTop;
        return rect.top + scrollTop;
      },
    },
    watch: {
      menuOpened() {
        if (this.menuOpened) {
          //document.documentElement.classList.add('locked');
          return;
        }
        //document.documentElement.classList.remove('locked');
        //setTimeout(() => document.documentElement.classList.remove('locked'), 500);
        //document.documentElement.classList.remove('locked');
      },
    },

    mounted() {
      this.lastScrolledPos = window.pageYOffset;
    },
  };
</script>

